/* eslint-disable */

<template>
  <div>
    <h3>Utilisation des Disques</h3>
    <PieChart :chartData="chartData" />
  </div>
</template>

<script>
import { Pie } from 'vue-chartjs';
import { Chart as ChartJS, Title, Tooltip, Legend, ArcElement } from 'chart.js';
import { defineComponent, computed } from 'vue';

// Enregistrement des composants Chart.js
ChartJS.register(Title, Tooltip, Legend, ArcElement);

export default defineComponent({
  name: 'DiskPieChart',
  props: {
    diskUsage: {
      type: Object,
      required: true
    }
  },
  components: {
    PieChart: Pie
  },
  setup(props) {
    // Préparation des données pour le graphique
    const chartData = computed(() => {
      // Vérifiez si diskUsage est défini et contient des données
      if (!props.diskUsage || Object.keys(props.diskUsage).length === 0) {
        console.warn('Disk usage data is empty or undefined');
        return { labels: [], datasets: [] }; // Retourner des données vides si non valide
      }

      const labels = Object.keys(props.diskUsage);
      const usedSpace = Object.values(props.diskUsage).map(disk => disk.used || 0);
      const freeSpace = Object.values(props.diskUsage).map(disk => disk.free || 0);

      // Calcul des données totales utilisées et libres
      const totalUsed = usedSpace.reduce((acc, val) => acc + val, 0);
      const totalFree = freeSpace.reduce((acc, val) => acc + val, 0);
      
      return {
        labels: ['Utilisé', 'Libre'], // Utiliser les labels définis
        datasets: [
          {
            label: 'Utilisation des Disques (Go)',
            backgroundColor: ['#FF6384', '#36A2EB'],
            data: [totalUsed, totalFree]
          }
        ]
      };
    });

    return {
      chartData
    };
  }
});
</script>

